@DesignSpec(width: 750, height: 1624)
abstract class GemsRewardsDesignSpec implements BasicDesignSpec {
  @SpecRect.fromLTRB(SpecHorizontal(12), CombinedSpec(SpecStatusBarHeight(50), SpecVertical(8)),
      SpecHorizontal(212), CombinedSpec(SpecStatusBarHeight(50), SpecVertical(66)))
  Rect get gemsBarBoundary;

  @SpecVertical(1)
  double get dp1;

  @SpecVertical(2)
  double get dp2;

  @SpecHeight(52)
  double get gemsSize;

  @SpecHeight(48)
  double get addSize;

  @SpecAbsoluteFontSize(24)
  double get gemsFontSize;

  GemBarSpec buildGemBarSpec() {
    final left = gemsBarBoundary.left;
    final boundary = gemsBarBoundary;

    // 高出1px
    final bg1 = RRect.fromLTRBR(left, boundary.top, boundary.right, boundary.bottom - dp2,
        Radius.circular(boundary.height / 2));

    // 低出2px
    final bg2 = RRect.fromLTRBR(left, boundary.top + dp1, boundary.right, boundary.bottom,
        Radius.circular(boundary.height / 2));

    final bg3 = RRect.fromLTRBR(left, boundary.top + dp1, boundary.right, boundary.bottom - dp2,
        Radius.circular(boundary.height / 2));

    final padding = (boundary.height - gemsSize) / 2;
    final gemRect =
        Rect.fromLTWH(boundary.left + padding, boundary.top + padding, gemsSize, gemsSize);

    final addPadding = (boundary.height - addSize) / 2;
    final addRect = Rect.fromLTWH(
        boundary.right - addPadding - addSize, boundary.top + addPadding, addSize, addSize);

    return GemBarSpec(boundary, gemRect, addRect, gemsFontSize, bg1, bg2, bg3);
  }

